/*global chrome*/

import React from "react";
import Paper from "@material-ui/core/Paper";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";

import AssessmentIcon from "@material-ui/icons/Assessment";
import UnfoldMoreIcon from "@material-ui/icons/UnfoldMore";
import TimelapseIcon from "@material-ui/icons/Timelapse";
import FormatLineSpacingIcon from "@material-ui/icons/FormatLineSpacing";
import DescriptionIcon from "@material-ui/icons/Description";
import SettingsIcon from "@material-ui/icons/Settings";
import DeleteIcon from "@material-ui/icons/Delete";

import { MessagesTab, PostsTab } from "./TradeSTabs";
import {
    Breadcrumbs,
    List,
    ListItem,
    ListItemAvatar,
    ListItemText,
    Button,
    ButtonGroup,
} from "@material-ui/core";

class TradeSContent extends React.Component {
    render = () => {
        switch (this.props.currentPage) {
            case 0:
                return <MessagesContent />;
            case 1:
                return <PostsContent />;
            case 2:
                return <FormatContent />;
            case 3:
                return <AccountContent />;
            default:
                return <Typography>Default page</Typography>;
        }
    };
}

function OPTabs(props) {
    const [value, setValue] = React.useState(0);
    const handleChange = (event, newValue) => {
        setValue(newValue);
        props.tabChanged(newValue);
    };
    return (
        <Paper>
            <Tabs value={value} onChange={handleChange}>
                <Tab label="Olymp Trade" />
                <Tab label="Pocket Option" />
            </Tabs>
        </Paper>
    );
}

class MessagesContent extends React.Component {
    constructor(props) {
        super(props);
        this.state = { index: 0 };
    }

    tabChanged = (index) => {
        this.setState((state) => {
            state.index = index;
            return state;
        });
    };

    currentTab = () => {
        return <MessagesTab platform={this.state.index} />;
    };

    render = () => {
        return (
            <>
                <OPTabs tabChanged={this.tabChanged} />
                {this.currentTab()}
            </>
        );
    };
}

class PostsContent extends React.Component {
    constructor(props) {
        super(props);
        this.state = { index: 0 };
    }

    tabChanged = (index) => {
        this.setState((state) => {
            state.index = index;
            return state;
        });
    };

    render = () => {
        return (
            <>
                <OPTabs tabChanged={this.tabChanged} />
                <PostsTab platform={this.state.index} />
            </>
        );
    };
}

class FormatContent extends React.Component {
    constructor(props) {
        super(props);
        this.state = { loaded: false, buttons: undefined };
    }

    getButtons = (callback) => {
        var self = this;
        var cb = callback;
        chrome.storage.local.get(["buttons"], (r) => {
            var buttons = r.buttons
                ? r.buttons
                : [
                      [
                          { type: "ass" },
                          { type: "exp" },
                          { type: "dir" },
                          { type: "text", text: "готовим" },
                      ],
                      [
                          { type: "ass" },
                          { type: "exp" },
                          { type: "dir" },
                          { type: "text", text: "СТАВИМ!!!" },
                      ],
                  ];
            try {
                cb(buttons);
            } catch (e) {}
        });
    };

    updateButtons = (buttons, callback) => {
        var cb = callback;
        chrome.storage.local.set({ buttons: buttons }, () => {
            try {
                cb(buttons);
            } catch (e) {}
        });
    };

    load = () => {
        this.getButtons((buttons) => {
            this.setState((state) => {
                state.buttons = buttons;
                state.loaded = true;
                return state;
            });
        });
    };

    addButton = () => {
        this.setState((state) => {
            state.loaded = true;
            state.buttons.push([{ type: "text", text: "Сигнал" }]);
            return state;
        });
    };

    getFormatAvatar = (type) => {
        switch (type) {
            case "ass":
                return <AssessmentIcon />;
            case "dir":
                return <UnfoldMoreIcon />;
            case "exp":
                return <TimelapseIcon />;
            case "stn":
                return <FormatLineSpacingIcon />;
            case "stv":
                return <FormatLineSpacingIcon />;
            case "text":
                return <DescriptionIcon />;
        }
    };

    getFormatText = (format) => {
        switch (format.type) {
            case "ass":
                return "Валютная пара";
            case "dir":
                return "Направление";
            case "exp":
                return "Экспирация";
            case "stn":
                return "Номер страйк-цены";
            case "stv":
                return "Значение страцк-цены";
            case "text":
                return "Текст";
        }
    };

    getFormatContent = (format, formatIndex, buttonIndex) => {
        if (format.type == "text") {
            return <TextField> </TextField>;
        }
        return this.getFormatText(format);
    };

    getButtonConstructor = (button, index) => {
        return (
            <Breadcrumbs separator="">
                {button.map((e, i) => {
                    return (
                        <ButtonGroup>
                            <Button>
                                {this.getFormatAvatar(e.type)}
                                {this.getFormatContent(e, i, index)}
                            </Button>
                            <Button>
                                <SettingsIcon />
                            </Button>
                            <Button>
                                <DeleteIcon />
                            </Button>
                        </ButtonGroup>
                    );
                })}
            </Breadcrumbs>
        );
    };

    getButtonsList = () => {
        return this.state.map((e, i) => {
            return (
                <List>
                    <ListItem
                        style={{
                            padding: 16,
                            paddingLeft: 25,
                            background: "#3f51b5",
                            color: "white",
                        }}
                    >
                        {this.getButtonConstructor(e, i)}
                    </ListItem>
                </List>
            );
        });
    };

    render = () => {
        return <>{this.getButtonsList()}</>;
    };
}

class AccountContent extends React.Component {
    render = () => {
        return <>account</>;
    };
}

class buttonFormat {
    constructor() {
        this.items = [
            { type: "ass" },
            { type: "exp" },
            { type: "dir" },
            { type: "text", text: "СТАВИМ!!!" },
        ];
    }

    restore = (ass, dir, exp, stn, stv) => {
        return this.items
            .map((e) => {
                switch (e.type) {
                    case "ass":
                        return ass;
                    case "dir":
                        return dir;
                    case "exp":
                        return exp;
                    case "stn":
                        return stn;
                    case "stv":
                        return stv;
                    case "text":
                        return e.text;
                }
            })
            .join(" ");
    };

    add = (type, text) => {
        this.items.push(
            type == text ? { type: type, text: text } : { type: type }
        );
    };

    delete = (index) => {
        this.items.splice(index);
    };

    update = (index, type, text) => {
        this.items[index] =
            type == text ? { type: type, text: text } : { type: type };
    };

    static fromItems = (items) => {
        var b = buttonFormat();
        b.items = items;
        return b;
    };
}

export default TradeSContent;
