import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import SwipeableDrawer from "@material-ui/core/SwipeableDrawer";
import Button from "@material-ui/core/Button";
import List from "@material-ui/core/List";
import Divider from "@material-ui/core/Divider";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import EmailIcon from "@material-ui/icons/Email";
import WallpaperIcon from "@material-ui/icons/Wallpaper";
import AccountBoxIcon from "@material-ui/icons/AccountBox";
import RadioButtonCheckedIcon from "@material-ui/icons/RadioButtonChecked";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";

class Page extends React.Component {
    constructor(props) {
        super(props);

        this.state = { open: false };
    }

    onPanelSwitch = (open) => (event) => {
        if (open === null) {
            open = !this.state.open;
        }
        if (
            event &&
            event.type === "keydown" &&
            (event.key === "Tab" || event.key === "Shift")
        ) {
            return;
        }
        this.setState({ open: open });
    };

    render = () => {
        return (
            <>
                <AppBar position="static">
                    <Toolbar>
                        <IconButton
                            edge="start"
                            color="inherit"
                            aria-label="menu"
                            onClick={this.onPanelSwitch(true)}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6">Olympcepy</Typography>
                    </Toolbar>
                </AppBar>
                <SwipeableDrawer
                    open={this.state.open}
                    onOpen={this.onPanelSwitch(true)}
                    onClose={this.onPanelSwitch(false)}
                >
                    <PageMenu page={this} />
                </SwipeableDrawer>
            </>
        );
    };
}

class PageMenu extends React.Component {
    constructor(props) {
        super(props);
        this.state = { page: props.page };
    }
    render = () => {
        return (
            <>
                <List style={{marginTop: -5}}>
                    <ListItem style={{padding: 15}} button key={"Меню настроек"}>
                        <ListItemIcon>
                            <MenuIcon />
                        </ListItemIcon>
                        <ListItemText primary={'Меню настроек'} />
                    </ListItem>
                </List>
                <Divider />
                <label style={{ textAlign: "center" }}> Отправка </label>
                <Divider />
                <List>
                    {["Сообщения", "Записи"].map((text, index) => (
                        <ListItem button key={text}>
                            <ListItemIcon>
                                {index % 2 === 0 ? (
                                    <EmailIcon />
                                ) : (
                                    <WallpaperIcon />
                                )}
                            </ListItemIcon>
                            <ListItemText primary={text} />
                        </ListItem>
                    ))}
                </List>
                <Divider />
                <label style={{ textAlign: "center" }}>Другие настройки</label>
                <Divider />
                <List>
                    {["Кнопки", "Дополнительно"].map((text, index) => (
                        <ListItem button key={text}>
                            <ListItemIcon>
                                {index % 2 === 0 ? (
                                    <RadioButtonCheckedIcon />
                                ) : (
                                    <AccountBoxIcon />
                                )}
                            </ListItemIcon>
                            <ListItemText primary={text} />
                        </ListItem>
                    ))}
                </List>
            </>
        );
    };
}

export default Page;
