class VKMethods {

    static chatsRecieved = (callback) => (event) => {
        var response = JSON.parse(event.target.responseText)
        if (response.error) {
            callback(response)
            return
        }
        callback(response)
    }

    static getGroupChats = (groupToken, callback) => {
        var xhr = new XMLHttpRequest();
        xhr.open(
            "GET",
            `https://api.vk.com/method/messages.getConversations?access_token=${groupToken}&v=5.120`
        );
        xhr.onload = VKMethods.chatsRecieved(callback)
    };
}

export default VKMethods;
