import React from "react";
import CircularProgress from "@material-ui/core/CircularProgress";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import EditIcon from "@material-ui/icons/Edit";
import AddIcon from "@material-ui/icons/Add";

var chrome 

class MessagesTab extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
        chrome.storage.local.get(["settsData"], function (r) {
            data = r.settsData;
            if (
                data.messages &
                data.messages[this.props.platform] &
                data.messages[this.props.platform].groupToken
            ) {
                this.setState((state) => {
                    state.hasToken = true;
                    state.groupToken = pData.groupToken;
                    return state
                });
            } else {
                this.setState((state) => {
                    state.hasToken = false;
                    state.groupToken = undefined
                    return state
                });
            }
        });
    }

    render = () => {
        if (this.state.hasToken) {
            return (
                <>
                    <List>
                        <ListItem button>
                            <ListItemIcon>
                                <EditIcon />
                            </ListItemIcon>
                            <ListItemText
                                primary={
                                    this.state.groupToken.slice(0, 5) + "..."
                                }
                            />
                        </ListItem>
                    </List>
                </>
            );
        } else if (this.state.hasToken != undefined) {
            <>
                <List>
                    <ListItem button>
                        <ListItemIcon>
                            <AddIcon />
                        </ListItemIcon>
                        <ListItemText primary={"Укажите API-ключ группы"} />
                    </ListItem>
                </List>
            </>;
        }
        return <CircularProgress style={{ textAlign: "center" }} />;
    };
}

export default MessagesTab;
