/*global chrome*/

import React from "react";
import CircularProgress from "@material-ui/core/CircularProgress";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import EditIcon from "@material-ui/icons/Edit";
import AddIcon from "@material-ui/icons/Add";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";

import VKMethods from "./VKMethods";

class APIKeyDialog extends React.Component {
    constructor(props) {
        super(props);
        this.state = { key: "" };
    }

    onOk = () => {
        this.props.onOk(this.state.key);
    };

    onCancel = () => {
        this.props.onCancel();
    };

    keyChanged = (event) => {
        if (event.currentTarget) {
            var newKey = event.currentTarget.value;
            this.setState((state) => {
                state.key = newKey;
                return state;
            });
        }
    };

    render = () => {
        return (
            <Dialog
                open={this.props.open}
                onClose={this.switchDialog}
                aria-labelledby="form-dialog-title"
            >
                <DialogTitle>Введите API-ключ группы</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        Для обеспечения возможности отправки сообщений в чаты
                        необходимо указать API-ключ группы.
                    </DialogContentText>
                    <TextField
                        autoFocus
                        margin="dense"
                        label="API-ключ группы"
                        fullWidth
                        onChange={this.keyChanged}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={this.onCancel} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={this.onOk} color="primary">
                        Ok
                    </Button>
                </DialogActions>
            </Dialog>
        );
    };
}

class MessagesTab extends React.Component {
    constructor(props) {
        super(props);
        this.state = { openDialog: false };
    }

    openDialog = () => {
        console.log("opening");
        this.setState((state) => {
            state.openDialog = true;
            return state;
        });
    };

    closeDialog = () => {
        this.setState((state) => {
            state.openDialog = false;
            return state;
        });
    };

    loadChats = () => {
        var self = this;
        chrome.storage.local.get(["settsData"], function (r) {
            if (
                r.settsData != undefined &&
                r.settsData.messages &&
                r.settsData.messages[this.props.platform] &&
                r.settsData.messages[this.props.platform].groupToken
            ) {
                self.gotToken(
                    r.settsData.messages[this.props.platform].groupToken
                );
            } else {
                self.setState((state) => {
                    state.hasToken = false;
                    state.groupToken = undefined;
                    return state;
                });
            }
        });
    };

    gotToken = (token) => {
        self.setState((state) => {
            state.hasToken = true;
            state.groupToken = token;
            state.openDialog = false;
            return state;
        });
    };

    render = () => {
        if (this.state.hasToken == undefined) {
            this.loadChats();
            return <CircularProgress style={{ padding: 20 }} />;
        } else if (this.state.hasToken) {
            return (
                <>
                    <List>
                        <ListItem button onClick={this.openDialog}>
                            <ListItemIcon>
                                <EditIcon />
                            </ListItemIcon>
                            <ListItemText
                                primary={
                                    this.state.groupToken.slice(0, 5) + "..."
                                }
                            />
                        </ListItem>
                    </List>
                    <APIKeyDialog
                        open={this.state.openDialog}
                        onCancel={this.closeDialog}
                        onOk={this.gotToken}
                    />
                    <ChatSelector
                        platfrom={this.props.platform}
                        token={this.state.groupToken}
                    />
                </>
            );
        } else {
            return (
                <>
                    <List>
                        <ListItem button onClick={this.openDialog}>
                            <ListItemIcon>
                                <AddIcon />
                            </ListItemIcon>
                            <ListItemText primary={"Укажите API-ключ группы"} />
                        </ListItem>
                    </List>
                    <APIKeyDialog
                        open={this.state.openDialog}
                        onCancel={this.closeDialog}
                        onOk={(key) => {
                            console.log(key);
                            this.closeDialog();
                        }}
                    />
                </>
            );
        }
    };
}

class ChatSelector extends React.Component {
    constructor(props) {
        super(props);
        this.state = { loaded: false };
    }

    render = () => {
        if (this.state.loaded) {
            return <div>test</div>;
        } else {
            return <CircularProgress style={{ padding: 20 }} />;
        }
    };
}

export default MessagesTab;
